        public bool checkMat()
        {
            if (this.Type >= 71 && this.Type <= 74)
            {
                this.Material = false;
                return false;
            }
            for (int i = 0; i < Recipe.numRecipes; i++)
            {
                int num = 0;
                while (Main.recipe[i].requiredItem[num].Type > 0)
                {
                    if (this.Name == Main.recipe[i].requiredItem[num].Name)
                    {
                        this.Material = true;
                        return true;
                    }
                    num++;
                }
            }
            this.Material = false;
            return false;
        }

