        public bool IsTheSameAs(Item compareItem)
        {
            return this.Name == compareItem.Name;
        }
        public bool IsNotTheSameAs(Item compareItem)
        {
            return this.Name != compareItem.Name || this.Stack != compareItem.Stack;
        }
